import { useState } from 'react';
import { Dialog, Box, TextField, Typography, Button, styled } from '@mui/material';



const Component = styled(Box)`
    height: 70vh;
    width: 90vh;
    
`;

const Image = styled(Box)`
     background: #2874f0 url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png) center 85% no-repeat;
     height: 83%;
     width: 28%;
     padding: 45px 35px;
     & > p, & > h5 {
        color:#FFFFFF;
        font-weight: 600;
     }
`;

const Wrapper = styled(Box)`
    display: flex;
    flex-direction: column;
    padding: 25px 35px;
    flex: 1;
    & > div, & > button, & > p {
        margin-top: 20px;
    }
`;

const LoginButton = styled(Button)`
    text-transform: none;
    background: #FB641B;
    color: #fff;
    height: 48px;
    border-radius: 2px;
`;

const  RequestOtp = styled(Button)`
    text-transform: none;
    background: #fff;
    color: #2874f0;
    height: 48px;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgb(0 0 0/ 20%);
`;

const Text = styled(Typography)`
    font-size: 12px;
    color: #878787;
`;

const CreateAccount = styled(Typography)`
     font-size: 14px;
     text-align: center;
     color: #2874f0;
     font-weight: 600;
     cursor: pointer;
`;

const LoginDialog = ({open, setOpen}) => {

const accountInitialValues = {
    login: {
        view: 'login',
        heading: 'Login',
        subHeading: 'Get access to your Orders, wishlist and Recommendations'
    },

    signup: {
        view: 'signup',
        heading: "Looks like you're new here!",
        subHeading: 'Sign up with your mobile number to get started'
    }
}

    const [account, toggleAccount] = useState(accountInitialValues.login)

    const handelClose = () => {
        setOpen(false);
        toggleAccount(accountInitialValues.login); 
    }

    const toggleSignup = () => {
        toggleAccount(accountInitialValues.signup)
    }
    return (
            
            <Dialog open = {open} onClose={handelClose} PaperProps ={{ sx: { maxWidth: 'unset'}}}>
                <Component>
                <Box style ={{ display: 'flex', height: '100%'}}> 
                    <Image>
                        <Typography variant="h5">{account.heading}</Typography>
                        <Typography style={{ marginTop: 20}}> {account.subHeading} </Typography>
                    </Image>
                    { 
                        account.view === 'login' ?
                        <Wrapper>
                            <TextField variant="standard" label="Enter Email/Mobile number"/>
                            <TextField variant="standard" label="Enter Password"/>
                            <Text>By continuing, you agree to flipkart's Terms of use and Privacy Policy.</Text>
                            <LoginButton>Login</LoginButton>
                            <Typography style={{ textAlign: 'center'}}>OR</Typography>
                            <RequestOtp>Request OTP</RequestOtp>
                            <CreateAccount onClick={() => toggleSignup()}>New to Flipkart? Create an account</CreateAccount>
                        </Wrapper>
                    :
                        <Wrapper>
                            <TextField variant="standard" label="Enter Firstname"/>
                            <TextField variant="standard" label="Enter Lastname"/>
                            <TextField variant="standard" label="Enter Username"/>
                            <TextField variant="standard" label="Enter Email"/>
                            <TextField variant="standard" label="Enter Password"/>
                            <TextField variant="standard" label="Enter Phone"/>
                            
                            <LoginButton>Continue</LoginButton>
                           
                        </Wrapper>
                    }    
                </Box>  
                </Component>
            </Dialog>
    )
}

export default LoginDialog;
